        "$:/plugins/twpub/8d938a73a5da09a5/text/000000758": {
            "role": "text",
            "title": "$:/plugins/twpub/8d938a73a5da09a5/text/000000758",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-8d938a73a5da09a5-1de308 twpub-8d938a73a5da09a5-1b2d9a\"><p class=\"kindle-cn-kai1\">5.1　解释相关研究中的第三变量问题</p></div>"
